query:
    query userContestRankingInfo($userSlug: String!) {
  userContestRanking(userSlug: $userSlug) {
    attendedContestsCount
    rating
    globalRanking
    localRanking
    globalTotalParticipants
    localTotalParticipants
    topPercentage
  }
  userContestRankingHistory(userSlug: $userSlug) {
    attended
    totalProblems
    trendingDirection
    finishTimeInSeconds
    rating
    score
    ranking
    contest {
      title
      titleCn
      startTime
    }
  }
}
